import React, { Component } from "react";
import DeleteBtn from "../components/DeleteBtn";
import Jumbotron from "../components/Jumbotron";
import API from "../utils/API";
import { Link } from "react-router-dom";
import { Col, Row, Container } from "../components/Grid";
import { List, ListItem } from "../components/List";
import { Input, TextArea, FormBtn } from "../components/Form";
import BookPage from "../components/BookPage";

class Books extends Component {
  state = {
    books: [],
    title: "",

    // search: ""
  };

  componentDidMount() {
    this.loadBooks();
  }
//res.data.items was the magic thing to get the default term to work
  loadBooks = () => {
    API.getBooks()
      .then(res =>
        this.setState({ books: res.data.items, title: "", author: "", synopsis: ""})
      )
      .catch(err => console.log(err));

  };
  search = query =>{
  API.search(query).then(resp => this.setState( 
    {books: resp.data.items,
     title: "",
    },

  ))};
    // if(!resp.ok){
    //   if(resp.status >=400 && resp.status < 500){
    //     return resp.json().then(data =>{
    //       let err ={errorMessage:data.message};
    //       throw err; 
    //     })
    //   }


  deleteBook = id => {
    API.deleteBook(id)
      .then(res => this.loadBooks())
      .catch(err => console.log(err));
  };

  handleInputChange = event => {
    const { name, value } = event.target;
    this.setState({
      [name]: value
    });
  };

  handleFormSubmit = event => {
    event.preventDefault();
    API.search(this.state.title)
    .then(res =>
      this.setState({ books: res.data.items, title: "", author: "", synopsis: ""})
    )
    .catch(err => console.log(err)); ;// if (this.state.title) {
      // console.log(this.state.title)// this.search(this.setState.title)        
      // .then(res.data => this.loadBooks())
      ;
  }
  handleFormSave = event => {
    event.preventDefault();
    alert("SAVED");
    API.saveBook(this.state.title)
    .then(res =>
      this.setState({ books: res.data.items, title: "", author: "", synopsis: ""})
    )
    .catch(err => console.log(err)); ;// if (this.state.title) {
      // console.log(this.state.title)// this.search(this.setState.title)        
      // .then(res.data => this.loadBooks())
      ;
  }




  render() {
    return (
      <div className = "container">

            <Jumbotron>
              <h1>What Books Should I Read?</h1>
            </Jumbotron>

            <form>
              <Input
                value={this.state.title}
                onChange={this.handleInputChange}
                name="title"
                placeholder="Search the Northwestern University Press catalog"
              />


              <FormBtn
                // disabled={!(this.state.author && this.state.title)}
                onClick={this.handleFormSubmit}
              >
                Submit Book
              </FormBtn>

            </form>


          <Col size="md-12"/>
            {/* <Jumbotron>
              <h1>Recent Publications in Philosophy</h1>
            </Jumbotron> */}
            {this.state.books.length ? (
              <div>
              {this.state.books.map(book =>(
                <BookPage key={book._id}
                title={book.volumeInfo.title}
                author ={book.volumeInfo.authors}
                image={book.volumeInfo.imageLinks.thumbnail}
                description={book.volumeInfo.description}
                link={book.volumeInfo.canonicalVolumeLink}
                onClick={this.handleFormSave}
                />

              ))}
</div>

            ) 

            :(
              <h3>No Results to Display</h3>
            )}




</div>

          )
        }} 
export default Books;
